import React from 'react'
import { makeStyles } from '@material-ui/core/styles'
import clsx from 'clsx'
import Card from '@material-ui/core/Card'
import CardHeader from '@material-ui/core/CardHeader'
import CardContent from '@material-ui/core/CardContent'
import Collapse from '@material-ui/core/Collapse'
import Avatar from '@material-ui/core/Avatar'
import IconButton from '@material-ui/core/IconButton'
import Typography from '@material-ui/core/Typography'

import { red } from '@material-ui/core/colors'
import ExpandMoreIcon from '@material-ui/icons/ExpandMore'
import Container from '@material-ui/core/Container'
import Box from '@material-ui/core/Box'
import List from '@material-ui/core/List'
import ListItem from '@material-ui/core/ListItem'
import ListItemIcon from '@material-ui/core/ListItemIcon'
import ListItemText from '@material-ui/core/ListItemText'
import DraftsIcon from '@material-ui/icons/Drafts'
import LocationOnIcon from '@material-ui/icons/LocationOn'

import { Divider } from '@material-ui/core'

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
}))

function Contact(props) {
  const classes = useStyles()
  const [expanded, setExpanded] = React.useState(false)

  const handleExpandClick = () => {
    setExpanded(!expanded)
  }

  const contactData = props.posts.results[0]
  console.log('contactData', contactData)

  return (
    <Container maxWidth="sm">
      <Box my={7}>
        {props.posts.results.map((contact) => (

          <Card className={classes.root} style={{ marginBottom: 20 }} key={contact.id.email}>
            <CardHeader
              avatar={(
                <Avatar aria-label="recipe" className={classes.avatar} src={contact.picture.thumbnail}>
                  {contact.name.last[0]}
                </Avatar>
              )}
              action={(
                <IconButton
                  className={clsx(classes.expand, {
                    [classes.expandOpen]: expanded,
                  })}
                  onClick={handleExpandClick}
                  aria-expanded={expanded}
                  aria-label="show more"
                >
                  <ExpandMoreIcon />
                </IconButton>
              )}
              title={`${contact.name.title} ${contact.name.first} ${contact.name.last}`}
              subheader={contact.cell}
            />
          </Card>
        ))}

        <Card className={classes.root}>
          <CardHeader
            avatar={(
              <Avatar aria-label="recipe" className={classes.avatar} src={contactData.picture.thumbnail}>
                {contactData.name.last[0]}
              </Avatar>
            )}
            action={(
              <IconButton
                className={clsx(classes.expand, {
                  [classes.expandOpen]: expanded,
                })}
                onClick={handleExpandClick}
                aria-expanded={expanded}
                aria-label="show more"
              >
                <ExpandMoreIcon />
              </IconButton>
            )}
            title={`${contactData.name.title} ${contactData.name.first} ${contactData.name.last}`}
            subheader={contactData.cell}
          />
          <Divider />
          <Collapse in={expanded} timeout="auto" unmountOnExit>
            <List component="nav" aria-label="main mailbox folders">
              <ListItem button>
                <ListItemIcon>
                  <DraftsIcon />
                </ListItemIcon>
                <Typography variant="p" component="p">{contactData.email}</Typography>
              </ListItem>

              <ListItem button>
                <ListItemIcon>
                  <LocationOnIcon />
                </ListItemIcon>
                <Typography variant="p" component="p">
                  {`${contactData.location.city}, ${contactData.location.country}`}
                </Typography>
              </ListItem>

            </List>
          </Collapse>
        </Card>
      </Box>
    </Container>
  )
}

export default Contact
